mapper.select:
SELECT USERID, OPTIONSEQ, OPTIONNAME, EXPTYPEID, EXPREASONID, EXPDETAILREASON, EXPINSTNAME, (SELECT ADDRESS FROM TB_FAVORITOPTIONINFO_ADDRESS WHERE OPTIONSEQ = #{optionseq} AND ADDRESSTYPE = 1) MAILTO, (SELECT ADDRESS FROM TB_FAVORITOPTIONINFO_ADDRESS WHERE OPTIONSEQ = #{optionseq} AND ADDRESSTYPE = 2) MAILCC, (SELECT ADDRESS FROM TB_FAVORITOPTIONINFO_ADDRESS WHERE OPTIONSEQ = #{optionseq} AND ADDRESSTYPE = 2) MAILBCC, CREATEDT FROM TB_FAVORITOPTIONINFO WHERE USERID = #{userid}
AND OPTIONSEQ = #{optionseq}
AND EXPTYPEID = #{exptypeid}
ORDER BY ORDERNUM , OPTIONNAME ASC
